import React, { useState } from 'react';
import {
  Box,
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
  Paper,
  Typography
} from '@mui/material';
import { Game, Player } from '../types';

interface GameFormProps {
  onSubmit: (game: Omit<Game, 'id'>) => void;
}

const GameForm: React.FC<GameFormProps> = ({ onSubmit }) => {
  const [date, setDate] = useState<string>(new Date().toISOString().split('T')[0]);
  const [winner, setWinner] = useState<Player>('Frank');
  const [loser, setLoser] = useState<Player>('Danielle');
  const [winnerScore, setWinnerScore] = useState<string>('');
  const [loserScore, setLoserScore] = useState<string>('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!winnerScore || !loserScore) return;

    onSubmit({
      date,
      winner,
      loser,
      winnerScore: parseInt(winnerScore),
      loserScore: parseInt(loserScore)
    });

    // Reset form
    setWinnerScore('');
    setLoserScore('');
  };

  return (
    <Paper elevation={3} sx={{ p: 3 }}>
      <Typography variant="h6" gutterBottom>
        Nieuwe Game Toevoegen
      </Typography>
      <Box component="form" onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              type="date"
              label="Datum"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth>
              <InputLabel>Winnaar</InputLabel>
              <Select
                value={winner}
                label="Winnaar"
                onChange={(e) => setWinner(e.target.value as Player)}
              >
                <MenuItem value="Frank">Frank</MenuItem>
                <MenuItem value="Danielle">Danielle</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth>
              <InputLabel>Verliezer</InputLabel>
              <Select
                value={loser}
                label="Verliezer"
                onChange={(e) => setLoser(e.target.value as Player)}
              >
                <MenuItem value="Frank">Frank</MenuItem>
                <MenuItem value="Danielle">Danielle</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              type="number"
              label="Score Winnaar"
              value={winnerScore}
              onChange={(e) => setWinnerScore(e.target.value)}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              type="number"
              label="Score Verliezer"
              value={loserScore}
              onChange={(e) => setLoserScore(e.target.value)}
            />
          </Grid>
          <Grid item xs={12}>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
              disabled={!winnerScore || !loserScore}
            >
              Game Toevoegen
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Paper>
  );
};

export default GameForm;
