import React from 'react';
import {
  Grid,
  Paper,
  Typography,
  Box,
  LinearProgress
} from '@mui/material';
import { PlayerStats } from '../types';

interface StatsProps {
  stats: PlayerStats[];
}

const Stats: React.FC<StatsProps> = ({ stats }) => {
  const sortedStats = [...stats].sort((a, b) => b.winPercentage - a.winPercentage);

  return (
    <Paper elevation={3} sx={{ p: 3 }}>
      <Typography variant="h6" gutterBottom>
        Statistieken
      </Typography>
      <Grid container spacing={2}>
        {sortedStats.map((stat) => (
          <Grid item xs={12} key={stat.player}>
            <Box sx={{ mb: 2 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                <Typography variant="subtitle1">
                  {stat.player}
                </Typography>
                <Typography variant="subtitle1">
                  {stat.wins}W - {stat.losses}V ({stat.winPercentage.toFixed(1)}%)
                </Typography>
              </Box>
              <LinearProgress
                variant="determinate"
                value={stat.winPercentage}
                sx={{
                  height: 10,
                  borderRadius: 5,
                  backgroundColor: '#e0e0e0',
                  '& .MuiLinearProgress-bar': {
                    backgroundColor: stat.winPercentage >= 50 ? '#4caf50' : '#f44336',
                  },
                }}
              />
            </Box>
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
};

export default Stats;
